#include <vector>
#include <iostream>
#include <unordered_map>
#include <unordered_set>
#include <algorithm>

using namespace std;
class Solution
{
public:
  // dp 先求所有的 两数和，再求三数和
  vector<vector<int>> threeSum(vector<int> &nums)
  {
    // 保存结果
    vector<vector<int>> x = {};
    unordered_set<string> s = {};
    //记录元素的值和下标
    unordered_map<int, vector<int>> numsAndIdx = {};

    vector<int> nums2 = {};

    int ns = nums.size();
    for (int i = 0; i < ns; i++)
    {
      if (numsAndIdx.count(nums[i]) == 0)
      {
        vector<int> temp = {i};
        numsAndIdx.insert(make_pair(nums[i], temp));
        nums2.push_back(nums[i]);
      }
      else
      {
        if (numsAndIdx[nums[i]].size() > 3)
        {
          continue;
        }
        numsAndIdx[nums[i]].push_back(i);
        nums2.push_back(nums[i]);
      }
    }

    numsAndIdx.clear();
    ns = nums2.size();
    for (int i = 0; i < ns; i++)
    {
      if (numsAndIdx.count(nums2[i]) == 0)
      {
        vector<int> temp = {i};
        numsAndIdx.insert(make_pair(nums2[i], temp));
      }
      else if ((numsAndIdx.count(nums2[i]) < 4))
      {
        numsAndIdx[nums2[i]].push_back(i);
      }
    }

    for (int i = 0; i < ns; i++)
    {
      for (int j = i + 1; j < ns; j++)
      {
        if (numsAndIdx.count(-nums2[i] - nums2[j]) > 0)
        {
          vector<int> t = numsAndIdx[-nums2[i] - nums2[j]];
          vector<int> o = {nums2[i], nums2[j], nums2[t[0]]};
          sort(o.begin(), o.end());
          string s2 = to_string(o[0]) + "," + to_string(o[1]) + "," + to_string(o[2]);
          if (s.count(s2) > 0)
          {
            continue;
          }
          int ts = t.size();
          for (int k = ts - 1; k >= 0; k--)
          {
            if (t[k] != i && t[k] != j)
            {
              s.insert(s2);
              x.push_back(o);
              break;
            }
          }
        }
      }
    }
    return x;
  }

  //test map
  void showmap()
  {
    unordered_map<int, int> x = {{1, 2}, {3, 4}, {1, 5}};
    cout << x[1] << endl;
  }
};

